import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import Loading from "../../components/Loading";
import { URLSERVER } from "../../Helpers/Constants";
import Information from "./Information";

export default function TreatmentDetail() {
  const [load, setLoad] = useState(false);
  const [service, setService] = useState({});
  const navigate = useNavigate();

  const params = useParams();

  useEffect(() => {
    window.watsonAssistantChatOptions = {
          integrationID: "d6e6ffa1-00f2-4b35-860e-ca6624f746b2", // The ID of this integration.
          region: "us-south", // The region your integration is hosted in.
          serviceInstanceID: "bf1549ca-d360-423b-867f-6c6b6942c5da", // The ID of your service instance.
          onLoad: function(instance) { instance.render(); }
        };
        setTimeout(function(){
          const t=document.createElement('script');
          t.src="https://web-chat.global.assistant.watson.appdomain.cloud/versions/" + (window.watsonAssistantChatOptions.clientVersion || 'latest') + "/WatsonAssistantChatEntry.js";
          document.head.appendChild(t);
        });
})

  useEffect(() => {
    fetch(`${URLSERVER}api/treatments/getOneService/${params.id}`, {
      method: "GET",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      mode: "cors",
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.status !== "ok") {
          alert(data.status);
          navigate("/services");
        } else {
          setService(data.data);
          setLoad(true);
        }
      })
      .catch((err) => console.log(err));
  });

  return (
    <div className="flex flex-col items-center max-w-[1520px] min-w-[320px]">
      {load ? (
        <Information
          title={service.title}
          price={service.price}
          description={service.description}
          image={service.image}
        />
      ) : (
        <Loading />
      )}
    </div>
  );
}
