import { data } from "autoprefixer";
import { useState, useEffect } from "react";
import Loading from "../../components/Loading";
import { TIMEALERT, URLSERVER } from "../../Helpers/Constants";
import GoogleMapReact from 'google-map-react';

export default function Map() {
  const [isLoading, setIsLoading] = useState(true);
  var defaultMap = {
    center: {
      lat: 0,
      lng: 0,
    },
    zoom: 11
  };
  useEffect(() => {
    fetch(`${URLSERVER}api/map/getMap`, {
      method: 'GET',
      headers: {
          'Accept' : 'application/json',
          'Content-Type' : 'application/json'
      }, 
      mode: 'cors'
    })
    .then(res => res.json())
    .then(data => {
      defaultMap.center.lat = data[0].lat;
      defaultMap.center.lng = data[0].lon;
      setIsLoading(false);
      console.log(data)
      console.log(defaultMap);
    })
  }, []);
  if (isLoading) {
    return <Loading/>
  }
  return (
    <div className="flex justify-center my-8 align-center max-w-[1520px] min-w-[320px]">
      <div className="flex flex-row  h-[75vh] w-[80%] ">
        <div className="h-[100%] w-[50%]">
          <iframe
            title="hi"
            className="h-[100%] w-[100%] border-0"
            src="https://www.google.com/maps/embed?pb=!4v1652738669644!6m8!1m7!1s3zH3dhkd6MEr03Aw5hwaOA!2m2!1d-3.990930082019558!2d-79.20582015162954!3f98.12!4f-1.9899999999999949!5f0.7820865974627469"
            allowfullscreen=""
            loading="lazy"
            referrerpolicy="no-referrer-when-downgrade"
          ></iframe>
        </div>
        <div style={{ height: '100vh', width: '100%' }}>
          <GoogleMapReact
            defaultCenter={defaultMap.center}
            defaultZoom={defaultMap.zoom}
          >
          </GoogleMapReact>
        </div>
      </div>
    </div>
  );
}
